	public static function words($length) {
		$seed = preg_split("/\s/", self::$_seed);
		$seedLength = count($seed);
		$end = $seedLength - $length - 1;
		$start = rand(0, $end);
		$result = ucfirst(join(" ",array_slice($seed, $start, $length)));
		if($length < 5){
			return preg_replace("/[,\.]/", "", $result);
		}
		return $result.".";
	}

